<style>
.col-grid {
	@apply grid;
	--col-padding: 2rem;
	--contained-max-width: 75rem;
	--breakout-max-width: 90rem;
	--full-w: minmax(var(--col-padding), 1fr);
	--breakout: minmax(0, calc((var(--breakout-max-width) - var(--contained-max-width)) / 2));
	--contained: min(100% - (var(--col-padding) * 2), var(--contained-max-width));
	grid-template-columns:
		[full-w-start] var(--full-w)
		[breakout-start] var(--breakout)
		[contained-start] var(--contained) [contained-end]
		var(--breakout) [breakout-end]
		var(--full-w) [full-w-end];
}

.col-grid > * {
	@apply py-3
}

.col-grid > :not(.breakout, .breakout-l, .breakout-r, .full-w, .full-w-l, .full-w-r),
.full-w > :not(.breakout, .breakout-l, .breakout-r, .full-w, .full-w-l, .full-w-r) {
	grid-column: contained;
}

/* Handle contained grid elements with a background color */
.col-grid > :not(.breakout, .breakout-l, .breakout-r, .full-w, .full-w-r)[class*="bg-"],
.full-w > :not(.breakout, .breakout-l, .breakout-r, .full-w, .full-w-r)[class*="bg-"] {
	@apply px-4 py-3;
}

.col-grid > .breakout {
	grid-column: breakout;
}

.col-grid > .breakout-l {
	grid-column-start: breakout-start;
	grid-column-end: contained-end;
}

.col-grid > .breakout-r {
	grid-column-start: contained-start;
	grid-column-end: breakout-end;
}

.col-grid > .full-w,
.col-grid > .full-w-r,
.col-grid > .full-w-l {
	@apply grid;
	grid-column: full-w;
	grid-template-columns: inherit;
}
</style>
